var qs = require('querystring');

//send the html page
exports.sendHtml = function(res, html) {
	res.setHeader('Content-Type', 'text/html');
	res.setHeader('Content-Length', Buffer.byteLength(html));
	res.end(html);
};

//parse http post data
exports.parseReceivedData = function(req, callback) {
	var body = '';
	req.setEncoding('utf8');
	req.on('data', function(chuck){
		body += chuck;
	});

	req.on('end', function() {
		var data = qs.parse(body);
		callback(data);
	});
};

//generate the action form
exports.actionForm = function(id, path, label) {
	var html = '<form method="post" action="' + path +'" style="display:inline">' +
		'<input type="hidden" name="id" value="' + id + '">' +
		'<input type="submit" value="' + label + '">' +
		'</form>';
	return html;
};

//add a work record
exports.add = function(db, req, res) {
	exports.parseReceivedData(req, function(work) {
		var sql = "INSERT INTO work(hours, date, description) " +
			"VALUES (?, ?, ?)";
		var values = [work.hours, work.date, work.description];
		db.query(sql, values, function(err) {
			if (err) throw err;
			exports.show(db, res);
		});
	});
};

//delete a work record
exports.delete = function(db, req, res) {
	exports.parseReceivedData(req, function(work) {
		var sql = "DELETE FROM work WHERE id=?";
		var values = [work.id];
		db.query(sql, values, function(err) {
			if (err) throw err;
			exports.show(db, res);
		});
	});
}

//archive a work record
exports.archive = function(db, req, res) {
	exports.parseReceivedData(req, function(work) {
		var sql = "UPDATE work SET archived=1 WHERE id=?";
		var values = [work.id];
		db.query(sql, values, function(err) {
			if (err) throw err;
			exports.show(db, res);
		});
	});
}

//show not archived work records
exports.show = function(db, res, showArchived) {
	var sql = "SELECT * FROM work WHERE archived=?" + 
		" ORDER BY date DESC";
	var values = [(showArchived? 1 : 0)];
	db.query(sql, values, function(err, rows) {
		if (err) throw err;
		html = (showArchived)? '' : '<a href="/archived">Archived Work</a><br/>';
		html += exports.worksTableHtml(rows);
		html += exports.workFormHtml();
		exports.sendHtml(res, html);
	});
}

//show archived work records
exports.showArchived = function(db, res) {
	exports.show(db, res, true);
}

//generate work list table html
exports.worksTableHtml = function(rows) {
	var html = '<table border="1">';
	html += '<tr><th>DATE</th><th>HOURS</th><th>DESCRIPTION</th><th>OPERATION</th></tr>';
	for (var i in rows) {
		html += '<tr>';
		html += '<td>' + rows[i].date + '</td>';
		html += '<td>' + rows[i].hours + '</td>';
		html += '<td>' + rows[i].description + '</td>';
		html += '<td>';
		if (!rows[i].archived) {
			html +=  exports.workArchiveForm(rows[i].id) ;
		}
		html += exports.workDeleteForm(rows[i].id);
		html += '</td>';
		html += '</tr>';
	}
	html += '</table>';
	return html;
};

//generate the work form
exports.workFormHtml = function() {
	var html = '<form method="post" action="/">' + 
		'<p><label>Date(YYYY-MM-DD): </label><br/><input name="date" type="text"></p>' +
		'<p><label>Hours worked: </label><br/><input name="hours" type="text"></p>' +
		'<p><label>Description: </label><br/>' +
		'<textarea name="description"></textarea><p>' +
		'<p><input type="submit" value=" Add ">' +
		'</form>';
	return html;
};

//generate archive form
exports.workArchiveForm = function(id) {
	return exports.actionForm(id, '/archive', 'Archived');
}

//generate delete form
exports.workDeleteForm = function(id) {
	return exports.actionForm(id, '/delete', 'Delete');
}
